import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

// Импорт компонентов
import Leagues from './components/Leagues';
import Teams from './components/Teams';
import GameStatistics from './components/GameStatistics';
import GameLineup from './components/GameLineup';
import LikeGame from './components/LikeGame';
import PopularGames from './components/PopularGames';
import Register from './components/Auth/Register';
import Login from './components/Auth/Login';

const App = () => {
    return (
        <Router>
            <nav style={styles.navbar}>
                <Link to="/" style={styles.link}>Лиги</Link>
                <Link to="/teams" style={styles.link}>Команды</Link>
                <Link to="/games/popular" style={styles.link}>Популярные игры</Link>
                <Link to="/register" style={styles.link}>Регистрация</Link>
                <Link to="/login" style={styles.link}>Вход</Link>
            </nav>

            <div style={styles.container}>
                <Routes>
                    <Route path="/" element={<Leagues />} />
                    <Route path="/teams" element={<Teams />} />
                    <Route path="/games/popular" element={<PopularGames />} />
                    <Route path="/register" element={<Register />} />
                    <Route path="/login" element={<Login />} />

                    {/* Динамические маршруты для матчей */}
                    <Route path="/games/:id/statistics" element={<GameStatistics />} />
                    <Route path="/games/:id/lineup" element={<GameLineup />} />
                    <Route path="/games/:id/like" element={<LikeGame />} />
                </Routes>
            </div>
        </Router>
    );
};

// Стилизация навигации и контента
const styles = {
    navbar: {
        backgroundColor: '#333',
        padding: '10px',
        display: 'flex',
        justifyContent: 'space-around'
    },
    link: {
        color: 'white',
        textDecoration: 'none',
        fontSize: '18px'
    },
    container: {
        padding: '20px'
    }
};

export default App;
