import React, { useState, useEffect } from "react";
import { fetchGames } from "./api";

const LigaChamp = () => {
    const [games, setGames] = useState([]);
    const [error, setError] = useState(null);  // Добавляем состояние ошибки

    useEffect(() => {
        fetchGames()
            .then(data => {
                if (data.length === 0) {
                    setError("Нет данных о матчах");
                } else {
                    setGames(data);
                }
            })
            .catch(err => setError("Ошибка загрузки данных"));
    }, []);

    return (
        <div>
            <h2>Champions League Games</h2>
            {error && <p style={{ color: "red" }}>{error}</p>} {/* Показываем ошибку */}
            <ul>
                {games.map((game) => (
                    <li key={game.ID}>
                        {game.HomeTeam} vs {game.AwayTeam} - {game.Score}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default LigaChamp;
